import React from 'react';

function getInitials(name) {
  return name
    .split(' ')
    .map(word => word.charAt(0))
    .join('')
    .toUpperCase()
    .slice(0, 2);
}

function PlayerCard({ player, playerRole, canStartPrivateChat, onStartPrivateChat, socket }) {
  const isCurrentPlayer = player.id === socket?.id;
  const isEliminated = player.isEliminated;
  const isTraitor = player.role === 'traitor';
  const isFaithful = player.role === 'faithful';
  
  // Determine avatar class based on role and status
  let avatarClass = '';
  if (isEliminated) {
    avatarClass = 'eliminated';
  } else if (isTraitor) {
    avatarClass = 'traitor';
  } else if (isFaithful) {
    avatarClass = 'faithful';
  }

  return (
    <div className={`player-card ${isEliminated ? 'eliminated' : ''} ${isTraitor ? 'traitor' : ''} ${isFaithful ? 'faithful' : ''}`}>
      <div className={`player-avatar ${avatarClass}`}>
        {getInitials(player.name)}
      </div>
      
      <div className="player-name">
        {player.name}
        {isCurrentPlayer && <span style={{ color: '#ff6b6b', marginLeft: '8px' }}>(You)</span>}
      </div>
      
      <div className="player-status">
        {player.isHost && <span style={{ color: '#ffd700', marginRight: '8px' }}>ðŸ‘‘ Host</span>}
        {isEliminated && <span style={{ color: '#e74c3c' }}>ðŸ’€ Eliminated</span>}
        {!isEliminated && player.hasVoted && <span style={{ color: '#27ae60' }}>âœ“ Voted</span>}
      </div>
      
      {canStartPrivateChat && !isEliminated && !isCurrentPlayer && (
        <button 
          className="btn btn-secondary" 
          style={{
            marginTop: '12px', 
            fontSize: '12px', 
            padding: '8px 16px',
            width: '100%'
          }} 
          onClick={() => onStartPrivateChat(player.id)}
        >
          ðŸ’¬ Private Chat
        </button>
      )}
    </div>
  );
}

export default PlayerCard;
