import React, { useState, useEffect, useRef } from 'react';

function ChatSystem({ 
  gameState, 
  playerRole, 
  canChat, 
  chatRoom, 
  activeRoom, 
  roomMessages, 
  activePrivateChat, 
  privateMessages, 
  availableRooms,
  newRoomName,
  setNewRoomName,
  onSendMessage,
  onSendRoomMessage,
  onSendPrivateMessage,
  onCreateRoom,
  onJoinRoom,
  onStartPrivateChat,
  socket
}) {
  const [message, setMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [roomMessages, privateMessages]);

  // Typing indicator logic
  useEffect(() => {
    if (message && !isTyping) {
      setIsTyping(true);
      socket.emit('typing', { isTyping: true, roomId: activeRoom || chatRoom });
    } else if (!message && isTyping) {
      setIsTyping(false);
      socket.emit('typing', { isTyping: false, roomId: activeRoom || chatRoom });
    }

    // Clear typing indicator after 3 seconds of no typing
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
    if (message) {
      typingTimeoutRef.current = setTimeout(() => {
        setIsTyping(false);
        socket.emit('typing', { isTyping: false, roomId: activeRoom || chatRoom });
      }, 3000);
    }

    return () => {
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
    };
  }, [message, isTyping, socket, activeRoom, chatRoom]);

  // Listen for typing events
  useEffect(() => {
    const handleTyping = ({ playerId, playerName, isTyping: userTyping }) => {
      if (playerId === socket?.id) return;
      
      setTypingUsers(prev => {
        const newSet = new Set(prev);
        if (userTyping) {
          newSet.add(playerName);
        } else {
          newSet.delete(playerName);
        }
        return newSet;
      });
    };

    socket.on('userTyping', handleTyping);
    return () => socket.off('userTyping', handleTyping);
  }, [socket]);

  const handleSendMessage = () => {
    if (!message.trim()) return;
    
    if (activeRoom) {
      onSendRoomMessage();
    } else if (activePrivateChat) {
      onSendPrivateMessage();
    } else if (canChat) {
      onSendMessage(chatRoom, message);
      setMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const getCurrentChatTitle = () => {
    if (activeRoom) {
      const room = availableRooms.find(r => r.roomId === activeRoom);
      return `Room: ${room?.name || 'Unknown'}`;
    }
    if (activePrivateChat) {
      const player = gameState.players.find(p => p.id === activePrivateChat);
      return `Private: ${player?.name || 'Unknown'}`;
    }
    if (canChat) {
      return playerRole === 'traitor' ? "Traitors' Secret Chat" : 'Faithful Discussion';
    }
    return 'Chat';
  };

  const getCurrentMessages = () => {
    if (activeRoom) {
      return roomMessages;
    }
    if (activePrivateChat) {
      return privateMessages[activePrivateChat] || [];
    }
    return [];
  };

  const getInitials = (name) => {
    return name
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const renderMessage = (msg, index) => {
    const isOwnMessage = msg.playerId === socket?.id || msg.from === socket?.id;
    const isSystem = !msg.playerId && !msg.from;
    const player = gameState.players.find(p => p.id === (msg.playerId || msg.from));
    
    return (
      <div 
        key={index} 
        className={`chat-message ${isOwnMessage ? 'own-message' : 'other-message'}`}
        aria-label={isSystem ? 'System message' : `${msg.playerName} says: ${msg.message}`}
        style={{
          marginLeft: isOwnMessage ? 'auto' : '0',
          marginRight: isOwnMessage ? '0' : 'auto',
          maxWidth: '80%',
          display: 'flex',
          flexDirection: isOwnMessage ? 'row-reverse' : 'row',
          alignItems: 'flex-end',
          gap: '8px',
          marginTop: 8,
          marginBottom: 8,
        }}
      >
        {/* Avatar/Initials */}
        {!isSystem && (
          <div
            className="message-avatar"
            style={{
              width: 32,
              height: 32,
              borderRadius: '50%',
              background: isOwnMessage
                ? 'linear-gradient(45deg, #ff6b6b, #ee5a24)'
                : 'linear-gradient(45deg, #4ecdc4, #44a08d)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: 12,
              fontWeight: 'bold',
              color: 'white',
              flexShrink: 0,
              boxShadow: isOwnMessage
                ? '0 2px 8px #ff6b6b55'
                : '0 2px 8px #4ecdc455',
            }}
            aria-label={`${msg.playerName}'s avatar`}
          >
            {getInitials(msg.playerName)}
          </div>
        )}
        
        {/* Message Bubble */}
        <div
          style={{
            background: isSystem
              ? 'linear-gradient(90deg, #333 60%, #444 100%)'
              : isOwnMessage
              ? 'linear-gradient(90deg, #ff6b6b 60%, #ee5a24 100%)'
              : 'linear-gradient(90deg, #222 60%, #4ecdc4 100%)',
            color: isSystem ? '#ffd700' : isOwnMessage ? '#fff' : '#fff',
            borderLeft: isOwnMessage ? '4px solid #ff6b6b' : isSystem ? '4px solid #ffd700' : '4px solid #4ecdc4',
            boxShadow: isOwnMessage ? '0 2px 8px #ff6b6b55' : isSystem ? '0 2px 8px #ffd70055' : '0 2px 8px #4ecdc455',
            animation: 'message-pop-in 0.4s cubic-bezier(.4,2,.6,1)',
            fontWeight: isSystem ? 700 : 400,
            fontStyle: isSystem ? 'italic' : 'normal',
            borderRadius: 14,
            padding: '14px 18px',
            fontSize: 16,
            letterSpacing: 0.2,
            transition: 'background 0.3s, box-shadow 0.3s',
            position: 'relative',
            overflow: 'hidden',
          }}
        >
          {!isSystem && (
            <div className="player-name" style={{ color: isOwnMessage ? '#fff' : '#4ecdc4', fontWeight: 700, marginBottom: 2 }}>
              {msg.playerName}
            </div>
          )}
          <div className="message-text">
            {msg.message}
          </div>
          <div style={{ 
            fontSize: '10px', 
            color: 'rgba(255,255,255,0.5)', 
            marginTop: '4px',
            textAlign: isOwnMessage ? 'right' : 'left'
          }}>
            {new Date(msg.timestamp).toLocaleTimeString()}
          </div>
        </div>
      </div>
    );
  };

  const renderTypingIndicator = () => {
    if (typingUsers.size === 0) return null;
    
    const typingList = Array.from(typingUsers).join(', ');
    return (
      <div className="typing-indicator" style={{
        display: 'flex',
        alignItems: 'center',
        gap: '8px',
        padding: '8px 16px',
        color: 'rgba(255,255,255,0.7)',
        fontSize: '14px',
        fontStyle: 'italic',
      }}>
        <div className="typing-dots">
          <span className="dot" />
          <span className="dot" />
          <span className="dot" />
        </div>
        <span>{typingList} {typingUsers.size === 1 ? 'is' : 'are'} typing...</span>
      </div>
    );
  };

  return (
    <div className="chat-container" aria-label="Chat system">
      <div className="chat-header">
        <div className="chat-title">{getCurrentChatTitle()}</div>
        <div style={{ fontSize: '12px', color: 'rgba(255,255,255,0.6)' }}>
          {getCurrentMessages().length} messages
        </div>
      </div>

      {/* Chat Room Controls for Faithful */}
      {playerRole === 'faithful' && gameState.phase === 'faithful_discussion' && (
        <div className="chat-room-controls">
          <input
            className="input"
            type="text"
            placeholder="New room name"
            value={newRoomName}
            onChange={e => setNewRoomName(e.target.value)}
            style={{ flex: 1, maxWidth: '200px' }}
            aria-label="New chat room name"
          />
          <button 
            className="btn btn-secondary" 
            onClick={onCreateRoom}
            disabled={!newRoomName}
            style={{ fontSize: '12px', padding: '8px 16px' }}
            aria-label="Create chat room"
          >
            Create Room
          </button>
        </div>
      )}

      {/* Available Chat Rooms */}
      {playerRole === 'faithful' && gameState.phase === 'faithful_discussion' && availableRooms.length > 0 && (
        <div className="chat-room-list">
          {availableRooms.map(room => (
            <div key={room.roomId} className="chat-room-item">
              <span>{room.name}</span>
              <button 
                className="btn btn-secondary"
                onClick={() => onJoinRoom(room.roomId)}
                style={{ fontSize: '10px', padding: '4px 8px' }}
                aria-label={`Join chat room ${room.name}`}
              >
                Join
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Messages */}
      <div className="chat-messages" aria-live="polite">
        {getCurrentMessages().map((msg, index) => renderMessage(msg, index))}
        {renderTypingIndicator()}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="chat-input">
        <input
          className="input"
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={e => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          disabled={!canChat && !activeRoom && !activePrivateChat}
          aria-label="Type a message"
        />
        <button
          className="btn"
          onClick={handleSendMessage}
          disabled={!message.trim() || (!canChat && !activeRoom && !activePrivateChat)}
          aria-label="Send message"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatSystem;
